mapper.update:
update student_person
set dormitory = case id
	
		when #{item.id} then #{item.dormitory}
	
	end,
	room = case id
	
		when #{item.id} then #{item.room}
	
	end
	where id in 
	
		${item.id}

mapper.select:
select id, studentid, name, sex, birthday, birthpath, homepath, nation,
headimg,
dormitory,room, college, major, majorclass
from student_person where college = ${college} and major = ${major}
and majorclass = ${majorclass}

select *
from student_person
where dormitory = ${dormitory} and room = ${room}
